import React,{Component} from 'react';
import axios from 'axios';
class SingleComplain extends Component {
    state={
        result:[]
    }
    approvebutton = value =>{
        axios.get('http://127.0.0.1:8000/fircomplain/getcomplain/' +value).then(response=>this.setState({result:response.date}))
        alert("wait"+this.state.result["status"]);
        
    }
    render() {
        const complains = this.props.complains;
        return (
            
                complains.map(complain =>(
                    <tr>
                            
                            <td>{complain.id}</td>
                            <td>{complain.name}</td>
                            <td>{complain.age}</td>
                            <td>{complain.address}</td>
                            <td>{complain.incDT}</td>
                            <td>{complain.dateofreg}</td>
                            <td>{complain.typeComplaint}</td>
                            <td>{complain.section}</td>
                            <td>{complain.status}</td>
                            <td>
                                <form  >
                                    <input type="hidden" name="sno" value="" />
                                    <input type="hidden" name="cat" value="" />
                                    <button type="submit" name="approve" class="btn btn-outline-success" onClick={()=>this.approvebutton(complain.id)}>Approve</button>
                                    <button type="submit" name="reject"  class="btn btn-outline-danger" onClick={() => axios.delete('http://127.0.0.1:8000/fircomplain/getcomplain/'+complain.id).then(response=>console.log(response))}>Reject</button>
                                </form>
                            </td>
                            </tr>
                ))
            
        );
    }
}

export default SingleComplain;
